    public static void putToStart2(char[] chars) {
        for (int i = chars.length - 1, j = chars.length - 1; j > 0; --j){
            if (chars[j] == '0') {//交换i、j的对应值,并且i自增
                char tmp = chars[i];
                chars[i] = chars[j];
                chars[j] = tmp;
                i--;
            }
        }
        for (char c : chars) {
            System.out.println((int)c);
        }
    }


    @Test
    public void test02() {
        putToStart2(new char[]{4, 0, 7, 0, 2});
    }
